package IQ;import java.io.*;public class _6_OSTest_array_close {    public static void main(String[] args) throws IOException {        String fileFromName = "C:/Users/yriyl/Pictures/image/hi.jpg";        String fileToName = "C:/Users/yriyl/Pictures/image/hik.jpg";        InputStream in = null;        OutputStream out = null;        try {            in = new FileInputStream(fileFromName);            out = new FileOutputStream(fileToName);            copy(in, out);        } catch (IOException e) {            throw new IOException("kkkkkkkkkkk");        } finally {            closeQuietly(in);            close(out);        }    }    public static void copy(InputStream in, OutputStream out) throws IOException {        byte[] buff = new byte[4 * 1024];        int count;        while ((count = in.read(buff)) != -1) {            out.write(buff,0,count);            System.out.println(count);        }    }    public static void closeQuietly(InputStream in) {        if (in != null) {            try {                in.close();            } catch (IOException ignore) {            }        }    }    public static void close(OutputStream out) {        if (out != null) {            try {                out.flush();                out.close();            } catch (IOException ignore) {            }        }    }}